import React, { useState } from 'react';
import { supabase } from '../../lib/supabase';
import { useAuth } from '../../contexts/AuthContext';
import { Calendar, Smile, Meh, Frown, Angry, Heart, Activity, FileText, Save } from 'lucide-react';

export const ProgressForm: React.FC = () => {
  const { user } = useAuth();
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [mood, setMood] = useState('');
  const [cravingsLevel, setCravingsLevel] = useState(5);
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const moodOptions = [
    { value: 'excellent', label: 'Excellent', emoji: 'ðŸ˜Š' },
    { value: 'good', label: 'Good', emoji: 'ðŸ™‚' },
    { value: 'okay', label: 'Okay', emoji: 'ðŸ˜' },
    { value: 'bad', label: 'Bad', emoji: 'ðŸ˜”' },
    { value: 'terrible', label: 'Terrible', emoji: 'ðŸ˜¢' },
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user) return;

    setLoading(true);
    setSuccess(false);

    try {
      const { error } = await supabase
        .from('progress_logs')
        .insert([
          {
            user_id: user.id,
            date,
            mood,
            cravings_level: cravingsLevel,
            notes: notes.trim(),
          },
        ]);

      if (error) throw error;

      setSuccess(true);
      setMood('');
      setCravingsLevel(5);
      setNotes('');
      
      // Reset success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    } catch (error) {
      console.error('Error saving progress:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <div className="flex items-center gap-3 mb-6">
        <div className="w-10 h-10 bg-success-100 rounded-full flex items-center justify-center">
          <Activity className="w-5 h-5 text-success-600" />
        </div>
        <div>
          <h3 className="font-semibold text-gray-900">Log Your Progress</h3>
          <p className="text-sm text-gray-500">Track your daily recovery journey</p>
        </div>
      </div>

      {success && (
        <div className="bg-success-50 border border-success-200 text-success-700 px-4 py-3 rounded-lg mb-4">
          Progress logged successfully! Keep up the great work! ðŸ’ª
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-1">
            Date
          </label>
          <div className="relative">
            <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              id="date"
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="input-field pl-10"
              required
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            How are you feeling today?
          </label>
          <div className="grid grid-cols-5 gap-2">
            {moodOptions.map((option) => (
              <button
                key={option.value}
                type="button"
                onClick={() => setMood(option.value)}
                className={`p-3 rounded-lg border-2 transition-colors ${
                  mood === option.value
                    ? 'border-primary-500 bg-primary-50'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                <div className="text-2xl mb-1">{option.emoji}</div>
                <div className="text-xs font-medium">{option.label}</div>
              </button>
            ))}
          </div>
        </div>

        <div>
          <label htmlFor="cravings" className="block text-sm font-medium text-gray-700 mb-1">
            Cravings Level (1-10)
          </label>
          <div className="flex items-center gap-3">
            <input
              id="cravings"
              type="range"
              min="1"
              max="10"
              value={cravingsLevel}
              onChange={(e) => setCravingsLevel(parseInt(e.target.value))}
              className="flex-1"
            />
            <span className="text-sm font-medium text-gray-700 min-w-[2rem]">
              {cravingsLevel}
            </span>
          </div>
          <div className="flex justify-between text-xs text-gray-500 mt-1">
            <span>No cravings</span>
            <span>Intense cravings</span>
          </div>
        </div>

        <div>
          <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-1">
            Notes (optional)
          </label>
          <div className="relative">
            <FileText className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
            <textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              className="input-field pl-10 resize-none"
              rows={3}
              placeholder="How was your day? Any challenges or victories?"
            />
          </div>
        </div>

        <button
          type="submit"
          disabled={loading || !mood}
          className="btn-primary w-full disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {loading ? (
            <div className="flex items-center gap-2">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
              Saving...
            </div>
          ) : (
            <div className="flex items-center gap-2">
              <Save className="w-4 h-4" />
              Save Progress
            </div>
          )}
        </button>
      </form>
    </div>
  );
};
