interface PaymentRequest {
  amount: number;
  currency: string;
  email: string;
  first_name: string;
  last_name: string;
  phone_number: string;
  description: string;
}

interface PaymentResponse {
  success: boolean;
  message: string;
  checkout_url?: string;
  payment_id?: string;
}

export class PaymentService {
  private apiKey: string;
  private baseUrl: string;

  constructor() {
    this.apiKey = process.env.REACT_APP_INTASEND_API_KEY || '';
    this.baseUrl = 'https://sandbox.intasend.com/api/v1';
  }

  async createPayment(paymentData: PaymentRequest): Promise<PaymentResponse> {
    if (!this.apiKey) {
      return {
        success: false,
        message: 'Payment service not configured. Please contact support.',
      };
    }

    try {
      const response = await fetch(`${this.baseUrl}/payment/links/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Token ${this.apiKey}`,
        },
        body: JSON.stringify({
          amount: paymentData.amount,
          currency: paymentData.currency,
          email: paymentData.email,
          first_name: paymentData.first_name,
          last_name: paymentData.last_name,
          phone_number: paymentData.phone_number,
          description: paymentData.description,
          redirect_url: `${window.location.origin}/payment-success`,
          webhook_url: `${window.location.origin}/api/webhooks/payment`,
        }),
      });

      if (!response.ok) {
        throw new Error('Payment request failed');
      }

      const data = await response.json();
      
      return {
        success: true,
        message: 'Payment link created successfully',
        checkout_url: data.checkout_url,
        payment_id: data.payment_id,
      };
    } catch (error) {
      console.error('Payment error:', error);
      return {
        success: false,
        message: 'Failed to create payment. Please try again.',
      };
    }
  }

  async verifyPayment(paymentId: string): Promise<boolean> {
    if (!this.apiKey) return false;

    try {
      const response = await fetch(`${this.baseUrl}/payment/links/${paymentId}/`, {
        headers: {
          'Authorization': `Token ${this.apiKey}`,
        },
      });

      if (!response.ok) return false;

      const data = await response.json();
      return data.state === 'COMPLETED';
    } catch (error) {
      console.error('Payment verification error:', error);
      return false;
    }
  }
}

export const paymentService = new PaymentService();
